-- ----------------------------
-- Table structure for `crm_hotel`
-- ----------------------------
DROP TABLE IF EXISTS `crm_hotel`;
CREATE TABLE `crm_hotel` (
  `hotel_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '酒店ID',
  `hotel_city` varchar(40) NOT NULL COMMENT '所在城市',
  `hotel_name` varchar(100) NOT NULL COMMENT '酒店名称',
  `hotel_english_name` varchar(100) NOT NULL COMMENT '酒店英文名',
  `hotel_address` varchar(255) NOT NULL COMMENT '酒店地址',
  `hotel_link` varchar(100) NOT NULL COMMENT '酒店链接',
  `hotel_nights` smallint(3) NOT NULL COMMENT '入住天数',
  `hotel_star` tinyint(1) unsigned NOT NULL COMMENT '酒店星级',
  `hote_level` tinyint(1) unsigned NOT NULL COMMENT '酒店类别',
  `hotel_bed_type` varchar(40) NOT NULL COMMENT '酒店床型',
  `hotel_room_type` tinyint(1) unsigned NOT NULL COMMENT '酒店房型',
  `hotel_room_type_name` varchar(100) NOT NULL COMMENT '房间名称',
  `hotel_room_type_english_name` varchar(100) NOT NULL COMMENT '房间英文名',
  `hotel_info` text NOT NULL COMMENT '酒店介绍',
  `hotel_specifics` varchar(255) DEFAULT NULL COMMENT '酒店特色',
  PRIMARY KEY (`hotel_id`)
) ENGINE=InnoDB AUTO_INCREMENT=357 DEFAULT CHARSET=utf8 COMMENT='酒店信息表';

